DROP TABLE IF EXISTS `yb_teachers`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `yb_teachers` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(11) unsigned NOT NULL COMMENT '用户id',
  `school_id` int(11) unsigned NOT NULL DEFAULT 0 COMMENT '学校id',
  `contract_start_date` datetime DEFAULT NULL COMMENT '合同开始日期',
  `contract_end_date` datetime DEFAULT NULL COMMENT '合同结束日期',
  `contract_id` int(11) unsigned NOT NULL DEFAULT 0 COMMENT '合同id',
  `visa_id` int(11) unsigned NOT NULL DEFAULT 0 COMMENT '签证id',
  `entry_status` tinyint(1) unsigned NOT NULL DEFAULT 0 COMMENT '入职状态',
  `head_photo` varchar(255) NOT NULL DEFAULT '' COMMENT '头像地址',
  `intro_video` varchar(255) NOT NULL DEFAULT '' COMMENT '自我介绍视频',
  `full_name` varchar(100) NOT NULL DEFAULT '' COMMENT '姓名',
  `nationality_id` int(11) unsigned NOT NULL DEFAULT 0 COMMENT '国籍id',
  `gender` tinyint(1) unsigned NOT NULL DEFAULT 0 COMMENT '性别(0:未知1:男2:女)',
  `birthday` date DEFAULT NULL COMMENT '生日',
  `email` varchar(128) NOT NULL DEFAULT '' COMMENT '邮箱',
  `passport_number` varchar(100) NOT NULL DEFAULT '' COMMENT '护照号',
  `phone_prefix` varchar(10) NOT NULL DEFAULT '' COMMENT '国际电话区号',
  `phone` varchar(40) NOT NULL DEFAULT '' COMMENT '电话',
  `wechat` varchar(128) NOT NULL DEFAULT '' COMMENT '微信号',
  `skype` varchar(128) NOT NULL DEFAULT '' COMMENT 'skype',
  `intro` longtext NOT NULL,
  `degree` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '学历',
  `priority_cities` text NOT NULL COMMENT '期望工作城市',
  `industry_type` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '所属行业',
  `priority_school_types` text NOT NULL COMMENT '期望学校类型',
  `ideal_wage_min` varchar(20) NOT NULL DEFAULT '' COMMENT '最低期望薪资',
  `ideal_wage_max` varchar(20) NOT NULL DEFAULT '' COMMENT '最高期望薪资',
  `work_type` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '工作类型',
  `have_pets` tinyint(1) unsigned NOT NULL DEFAULT 0 COMMENT '是否携带宠物',
  `have_pets_description` varchar(255) NOT NULL DEFAULT '' COMMENT '携带宠物补充说明',
  `live_with_relatives` tinyint(1) unsigned NOT NULL DEFAULT 0 COMMENT '是否携带家属',
  `live_with_relatives_description` varchar(255) NOT NULL DEFAULT '' COMMENT '携带家属补充说明',
  `accommodation` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '期望住宿',
  `employment_status` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '就业情况',
  `visa_status` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '签证情况',
  `begin_work_time` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '可入职时间',
  `extra_begin_work_time` varchar(100) NOT NULL DEFAULT '' COMMENT '其他可入职时间',
  `work_experience_year` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '教学年限',
  `school_taughts` text NOT NULL COMMENT '曾任职学校类别',
  `current_visa_or_residence_permit_start_date` datetime DEFAULT NULL COMMENT '当前签证或居留许可证开始日期',
  `current_visa_or_residence_permit_end_date` datetime DEFAULT NULL COMMENT '当前签证或居留许可证截止日期',
  `mother_tongue` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '母语',
  `chinese_level` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '中文等级',
  `english_level` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '英语等级',
  `file_public` tinyint(1) unsigned NOT NULL DEFAULT 1 COMMENT '是否公开文件',
  `creator_id` int(11) unsigned NOT NULL DEFAULT 0 COMMENT '创建者id',
  `deleted_at` timestamp NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `creator_id` (`creator_id`)
) ENGINE=InnoDB AUTO_INCREMENT=329 DEFAULT CHARSET=utf8mb4 COMMENT='教师表';
